interface FeatureCardsProps {
  features?: Feature[]
  className?: string
}

export default function TeamPlaceholders({
  features = DEFAULT_FEATURES,
  className = '',
}: FeatureCardsProps) {
  return (
    <div aria-label="Key features" className={`w-full px-4 py-2 sm:px-6 lg:px-10 ${className}`}>
      <div className="mx-auto max-w-7xl">
        <div className="grid grid-cols-1 gap-8 md:grid-cols-3 md:gap-10">
          {features.map((f) => (
            <article
              key={f.id}
              className="flex flex-col items-center overflow-visible rounded-3xl p-6 text-center shadow-(--shadow-badge) transition-shadow duration-300 hover:shadow-[0px_12px_40px_rgba(0,0,0,0.10)]"
            >
              <div>
                <div
                  className="flex h-20 w-20 items-center justify-center rounded-2xl shadow-(--shadow-badge)"
                  aria-hidden
                >
                  <div className="m-2 p-1 text-black">{f.icon}</div>
                </div>
              </div>

              <h3 className="mt-2 text-center text-lg font-medium text-gray-900">{f.title}</h3>

              <p className="mt-3 max-w-[42ch] text-center text-sm leading-6 text-gray-600">
                {f.description}
              </p>
            </article>
          ))}
        </div>
      </div>
    </div>
  )
}
